import React, { Component } from 'react';
import Carousel from 'antd/es/carousel';
import KDYK from '../img/kdyk.jpg'
import KRK from '../img/krk.jpg'
import KMK from '../img/kmk.jpg'
import Item from 'antd/lib/list/Item';
export default class Ant extends Component {
    state={
        imgs:[{id:1, source:KRK,alt:"科洛克",urls:"https://www.skoda.com.cn/karoq/index.html"},
        {id:2, source:KDYK,alt:"柯迪亚克",urls:"https://www.skoda.com.cn/kodiaq/index.html"},
        {id:3, source:KMK,alt:"科米克",urls:"https://www.skoda.com.cn/kamiq/index.html"}
    ]
    }
    onclick(){
        console.log();
    }
    render() {
        return (
            <div className="App">
                <Carousel autoplay effect="fade" dotPosition="bottom">
                    {
                       this.state.imgs.map( (item)=> <img src={item.source} key={item.id} alt={item.alt}><a href={item.urls}></a></img>) 
                    }       x
                        
                </Carousel>
            </div>
        );
    }
}
